import React from 'react';
import './AccommodationBlock.less';
import { Badge, Image } from 'antd';
import _ from 'lodash';

type AccommodationType = {
  accommodation: {
    id: number,
    price: number,
    description: string,
    title: string,
    floor: number,
    maxFloor: number,
    roomCount: number,
    address: string,
    space: number,
    metro: {
      name: string,
      color: string,
      transportTime: number,
      transportType: string,
    }[]
    images:{ src: string }[]
    owner: {
      name: string,
      phoneNumber: string,
    }
  };
};

const AccommodationBlock: React.FC<AccommodationType> = ({ accommodation }: AccommodationType) => {
  const {
    id, price, description, title, floor, maxFloor, roomCount, address, space, images, owner, metro,
  } = accommodation;
  return (
    <div className="accommodation-view">
      <div className="title-block">
        <div>
          {title}
        </div>
        <div>
          <span className="price">
            {price.toLocaleString()}
          </span>
          ₽/мес
        </div>
      </div>
      <div className="address">
        {address}
      </div>
      <div className="metro-block">
        {metro.map((metroItem) => (
          <div className="metro-item" key={metroItem.name}>
            <Badge
              color={metroItem.color}
              text={(
                <span>
                  {metroItem.name}
                  <span className="transport-time">
                    {` (${metroItem.transportTime} минут ${_.lowerFirst(metroItem.transportType)})`}
                  </span>
                </span>
                )}
            />
          </div>
        ))}
      </div>
      <div className="image-block">
        <Image.PreviewGroup>
          {images.map((image) => (
            <Image key={image.src} width={250} height={250} src={image.src} />
          ))}
        </Image.PreviewGroup>
      </div>
    </div>
  );
};

export default AccommodationBlock;
